// Make a controlled form in a new component that takes in a string and when you hit submit button the component loads a message that says "Your input was X characters long"
// The message does not appear until the submit button has been hit once. The input should clear out when the submit button is hit.
// Create a controlled form with 1 input which is a string
// Add a submit button
// load a message with string length
// It should clear out our input

import React, { useState } from "react";

function StringForm() {
    const[string, setString] = useState('')
    const[input, setInput] = useState('')

    function onInputChange (e) {
        setInput(e.target.value)
    }

    function handleSubmit (e) {
        e.preventDefault()
        setString(input)
    }

    return (
        <form>
            <label>Enter String Here
                <input type="Text" value={input} onChange={onInputChange}/>
            </label>
            <button type="Submit" onClick={handleSubmit}>
                Submit
            </button>
        </form>
    )
}

export default StringForm
