// Make a controlled form in a new component that takes in a string and when you hit submit button the component loads a message that says "Your input was X characters long"
// The message does not appear until the submit button has been hit once. The input should clear out when the submit button is hit.
// 1. form, 1 input takes in a string
// 2. Button to submit the form
// 3. once submit is hit count the number of items in the string
// 4. Display the string "Your input was X characters long" with x being the number of characters.
import React from "react";

function stringLengthForm () {
    function handleSubmit() {
        return <p>Your input was x characters long</p>
    }

    return (
    <form>
        <label>String
            <input type='Text' />
            <button type="Submit" onClick={handleSubmit}>
                Submit
            </button>

        </label>
    </form>
    )
}

export default stringLengthForm
